mapper.select:
select udrs.data_rule_sys_id, udrs.data_rule_id, udrs.sys_key, up.sys_name from urc_data_rule_sys udrs LEFT JOIN urc_data_rule udr ON udrs.data_rule_id = udr.data_rule_id LEFT JOIN urc_permission up ON udrs.sys_key=up.sys_key
udr.user_name = #{userDO.userName}
AND udrs.sys_key IN
#{sysKey}

SELECT udrs.data_rule_id, udrs.data_rule_sys_id, udrs.create_time, udrs.sys_key, udr.user_name from urc_data_rule_sys udrs LEFT JOIN urc_data_rule udr ON udrs.data_rule_id = udr.data_rule_id
udrs.sys_key = #{sysKey}
and udrs.create_time > #{createTime}
AND udr.user_name is not null and udr.user_name !=''
ORDER BY udrs.create_time asc LIMIT #{pageSize}

SELECT udrs.data_rule_id, udrs.data_rule_sys_id, udrs.create_time, udrs.sys_key, udr.user_name from urc_data_rule_sys udrs LEFT JOIN urc_data_rule udr ON udrs.data_rule_id = udr.data_rule_id
udrs.sys_key = #{sysKey}
and udrs.create_time = #{createTime}
AND udr.user_name is not null and udr.user_name !=''
ORDER BY udrs.create_time asc

mapper.delete:
delete us,uc,ue from urc_data_rule_sys us left join urc_data_rule_col uc on us.data_rule_sys_id = uc.data_rule_sys_id left join urc_expression ue on us.data_rule_sys_id = ue.data_rule_sys_id where us.data_rule_id in
#{dataRuleId}
and us.create_by = #{createBy,jdbcType=VARCHAR}

delete us,uc,ue from urc_data_rule_sys us left join urc_data_rule_col uc on us.data_rule_sys_id = uc.data_rule_sys_id left join urc_expression ue on us.data_rule_sys_id = ue.data_rule_sys_id
us.data_rule_id = #{dataRuleId}
and us.sys_key in
#{sysKey}

mapper.select:
select sys_key from urc_data_rule ud left join urc_data_rule_sys us
 on ud.data_rule_id = us.data_rule_id
 where ud.user_name= #{userName,jdbcType=VARCHAR}
